, filter: {((pod_phase!="Failed")AND(pod_phase!="Succeeded")AND(k8s.workload.kind!="job")AND(k8s.workload.kind!="cronjob")) AND dt.entity.cloud_application == "CLOUD_APPLICATION-F568A257473A3CE2"}, by: {k8s.namespace.name,k8s.workload.kind,k8s.workload.name}
| join [timeseries operand=sum(dt.kubernetes.pods, default:0.0, rollup: avg), nonempty:true
, filter: {((pod_phase!="Failed")AND(pod_phase!="Succeeded")AND(k8s.workload.kind!="job")AND(k8s.workload.kind!="cronjob")AND(pod_condition!="Ready")) AND dt.entity.cloud_application == "CLOUD_APPLICATION-F568A257473A3CE2"}, by: {k8s.namespace.name,k8s.workload.kind,k8s.workload.name}], on: {interval}, fields: {o2=operand}
| fieldsAdd result=o1[]-o2[]
| fieldsRemove {o1,o2}

